mapper.update:
update article 


			title = #{title},


			articleType = #{articleType},


			summary = #{summary},


			content = #{content},



			lastUpdBy = #{lastUpdBy}


			lastUpdTime = #{lastUpdTime}


where articleId = #{articleId}

mapper.select:
select count(articleId) from article
where 1 = 1

		and articleType = #{articleType }


		and articleId = #{articleId}

select articleId, title, articleType, summary, createBy,createTime, lastUpdBy, lastUpdTime 
from article 
where 1 = 1

		and articleType = #{articleType }


		and articleId = #{articleId}

select *
from article
where articleId in (
	select articleId 
	from article_tags where tagId = #{tagId }
)

mapper.insert:
insert into clicks 
	(
		articleId,
		clicks
	) 
	values (
		#{articleId},
		#{clicks}
	)

mapper.update:
update clicks
set clicks = #{clicks } 

where articleId = #{articleId}
